mapper.select:
SELECT r.id, u.name as userName, u.email as userEmail, s.name as studioName, r.reservation_date as reservationDate, r.start_time as startTime, r.end_time as endTime, r.people_count as peopleCount, r.total_amount as totalAmount, r.status, r.created_at as createdAt, r.updated_at as updatedAt FROM "reservation" r INNER JOIN "user" u ON r.user_id = u.id INNER JOIN "studio" s ON r.studio_id = s.id
AND r.status = #{criteria.status}
AND r.reservation_date BETWEEN #{criteria.startDate} AND #{criteria.endDate}
AND r.user_id = #{criteria.userId}
AND r.studio_id = #{criteria.studioId}
AND ( LOWER(u.name) LIKE LOWER(CONCAT('%', #{criteria.searchKeyword}, '%')) OR LOWER(u.email) LIKE LOWER(CONCAT('%', #{criteria.searchKeyword}, '%')) OR LOWER(s.name) LIKE LOWER(CONCAT('%', #{criteria.searchKeyword}, '%')) )
ORDER BY ${criteria.orderBy} LIMIT #{criteria.limit} OFFSET #{criteria.offset}

SELECT COUNT(*)
FROM "reservation" r
INNER JOIN "user" u ON r.user_id = u.id
INNER JOIN "studio" s ON r.studio_id = s.id


        AND r.status = #{criteria.status}


        AND r.reservation_date BETWEEN #{criteria.startDate} AND #{criteria.endDate}


        AND r.user_id = #{criteria.userId}


        AND r.studio_id = #{criteria.studioId}


        AND (
            LOWER(u.name) LIKE LOWER(CONCAT('%', #{criteria.searchKeyword}, '%'))
            OR LOWER(u.email) LIKE LOWER(CONCAT('%', #{criteria.searchKeyword}, '%'))
            OR LOWER(s.name) LIKE LOWER(CONCAT('%', #{criteria.searchKeyword}, '%'))
        )

SELECT 
    r.id,
    s.name as studioName,
    r.reservation_date::text as date,
    r.start_time::text as startTime,
    r.end_time::text as endTime,
    LOWER(r.status) as status,
    r.total_amount as totalAmount
FROM "reservation" r
INNER JOIN "studio" s ON r.studio_id = s.id

    AND r.user_id = #{criteria.userId}

        AND r.status = #{criteria.status}


        AND r.reservation_date BETWEEN #{criteria.startDate} AND #{criteria.endDate}


        AND r.studio_id = #{criteria.studioId}


ORDER BY ${criteria.orderBy}
LIMIT #{criteria.limit} OFFSET #{criteria.offset}

SELECT COUNT(*)
FROM "reservation" r

    AND r.user_id = #{criteria.userId}

        AND r.status = #{criteria.status}


        AND r.reservation_date BETWEEN #{criteria.startDate} AND #{criteria.endDate}


        AND r.studio_id = #{criteria.studioId}
