WHERE studio_id = #{studioId}
  AND reservation_date BETWEEN #{start} AND #{end}

SELECT COALESCE(SUM(total_amount), 0) FROM "Reservation"
WHERE studio_id = #{studioId}
  AND reservation_date = #{today}

